
public class Gorilla extends Mammal{
	
	public Gorilla() {
		
		// TODO Auto-generated constructor stub
	}


	
	public int throwSomething() {
		
		return energyLevel -= 5;
	}
	
	public int eatBananas() {
		
		return energyLevel += 10; 
	}
	
	public int climb() {
		return energyLevel -= 10;
		
	}
}
